import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register({ setIsAuthenticated }) {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });

    const { username, password } = formData;
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('/api/v1/register', formData)
            .then((response) => {
                // localStorage.setItem('token', response.data.token); // Token is returned on login, not registration
                console.log('Registration successful:');
                // setIsAuthenticated(true); // Don't set authenticated after registration, require login
                navigate('/login'); // Redirect to login page after successful registration
            })
            .catch(error => {
                // console.error('Registration failed:', error);
            })
            .catch(error => {
                console.error('Registration failed:', error);
                // Handle registration error (e.g., display error message)
            });
    };

    return (
        <RegisterStyled>
            <form onSubmit={handleSubmit}>
                <h2>Register</h2>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Register</button>
            </form>
        </RegisterStyled>
    );
}

const RegisterStyled = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;

    form {
        padding: 2rem;
        border: 1px solid #ccc;
        border-radius: 8px;
    }
`;

export default Register;
